import React, { useState, useEffect } from "react";
import './App.css';
import { Card, CardContent, Input, LinearProgress } from "@mui/material";
import _ from 'lodash';
import axios from 'axios';

export default function App() {
  const titleArray = ['Banking', 'Logistic', 'E-Commerce', 'Computer'];
  const [searchTerm, setSearchTerm] = useState("");
  const [users, setUsers] = useState([]);
  const [isReady, setIsReady] = useState(false);

  useEffect(() => {
    axios
      .get("http://localhost:3001/api/user")
      .then((res) => {
        setUsers(res?.data?.rows);
        setIsReady(true); // Set isReady to true when data is fetched
        console.log("User", res?.data?.rows);
      })
      .catch((error) => {
        console.error("Error", error?.message);
      });

    return () => {};
  }, []);

  return (
    <div>
      {!isReady && <LinearProgress />} {/* Display LinearProgress while loading */}
      {isReady && (
        <Card>
          <CardContent>
            <div>Search Box</div>
            <Input
              placeholder='Input Some Search Word'
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <div>
              You Search <span className='text-blue-500'>{searchTerm}</span>
            </div>
          </CardContent>
        </Card>
      )}

      <div>
        <h3 className='font-bold'>User List</h3>
        {_.map(users, (eachUser, index) => (
          <div key={index}>
            {index + 1}. - {eachUser?.name}
          </div>
        ))}
      </div>

      <footer className="text-mt-8">
        This Footer
      </footer>
    </div>
  );
}
