import React, { useState, useEffect } from "react";
import { LinearProgress } from "@mui/material"; // Import LinearProgress from Material-UI

export default function App() {
  const [isReady, setIsReady] = useState(false);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Simulating API call delay for demonstration purposes
    setTimeout(() => {
      fetchUserData();
    }, 2000); 

    return () => {
      
    };
  }, []);

  const fetchUserData = () => {
    // Fetch user data from API
    fetch("http://localhost:3001/api/user")
      .then((response) => response.json())
      .then((data) => {
        setUsers(data?.rows || []);
        setIsReady(true); // Set isReady to true when data is fetched
      })
      .catch((error) => {
        console.error("Error fetching data:", error?.message);
        setIsReady(true); // Set isReady to true even if there's an error (for simplicity)
      });
  };

  return (
    <div>
      {!isReady && <LinearProgress />} 
      {isReady && (
        <>
          <h1>User List</h1>
          <ul>
            {users.map((user) => (
              <li key={user.id}>{user.name}</li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}
