import React, { useState, useEffect } from "react";
import './App.css';
import { Card, CardContent, Input, Table, Button, LinearProgress } from "@mui/joy";
import _ from 'lodash';
import axios from 'axios';
import { useForm, Controller } from "react-hook-form";

export default function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [users, setUsers] = useState([]);
  const [isReady, setIsReady] = useState(false);
  const { control, handleSubmit } = useForm();

  const handleDeleteUser = (userId) => {
    axios
      .delete("http://localhost:3001/api/user/" + userId)
      .then(() => {
        getAllUser();
      })
      .catch((error) => {
        alert(error?.message);
        console.error("Error", error?.message);
      });
  };

  const getAllUser = () => {
    setIsReady(false);
    axios
      .get(`${process.env.REACT_APP_API_URL}/user`)
      .then((res) => {
        setUsers(res?.data?.rows);
        setIsReady(true);
      })
      .catch((error) => {
        console.error("Error", error?.message);
      });
  };

  const handleCreateUser = (data) => {
    setIsReady(false);
    axios
      .post(`${process.env.REACT_APP_API_URL}/user`, data)
      .then(() => {
        getAllUser();
      })
      .catch((error) => {
        console.error("Error", error?.message);
      });
  };

  useEffect(() => {
    getAllUser();
  }, []);

  useEffect(() => {
    const filtered = users.filter((user) =>
      user.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredUsers(filtered);
  }, [searchTerm, users]);

  if (!isReady) {
    return (
      <div>
        <LinearProgress />
      </div>
    );
  }

  return (
    <div>
      <div className='min-h-screen'>
        <div className='flex justify-center '>
          <div className='lg:w-3/4'>
            <div className='my-1 font-semibold text-lg'>เพิ่มพนักงานใหม่</div>
            <Card>
              <CardContent>
                <form onSubmit={handleSubmit(handleCreateUser)}>
                  <div>ชื่อ</div>
                  <Controller
                    name='name'
                    control={control}
                    render={({ field }) => (
                      <Input {...field} placeholder='ชื่อพนักงาน' />
                    )}
                  />
                  <div>แผนก</div>
                  <Controller
                    name='department'
                    control={control}
                    render={({ field }) => (
                      <Input {...field} placeholder='แผนก' />
                    )}
                  />
                  <div>
                    <Button type="submit">บันทึก</Button>
                  </div>
                </form>
              </CardContent>
            </Card>
            <div className='my-4'>
              <div>ค้นหา</div>
              <Input
                placeholder='ค้นหาชื่อพนักงาน'
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
            <h3 className='font-bold'>User List</h3>
            <Table>
              <thead>
                <tr>
                  <th>ลำดับที่</th>
                  <th>ชื่อ</th>
                  <th>แผนก</th>
                  <th>ดำเนินการ</th>
                </tr>
              </thead>
              <tbody>
                {_.map(filteredUsers, (eachUser, index) => (
                  <tr key={index}>
                    <td>{index + 1}</td>
                    <td>{eachUser?.name}</td>
                    <td>{eachUser?.department}</td>
                    <td>
                      <Button color='danger' onClick={() => handleDeleteUser(eachUser?._id)}>ลบ</Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>
        </div>
        <footer className="text-mt-8">
          This Footer
        </footer>
      </div>
    </div>
  );
}
